%----------------------------------------------------%----------------- Parameters -----------------------%----------------------------------------------------SNR = 2000;%----------------------------------------------------%----------------- Calculation ----------------------%----------------------------------------------------Tx_Power = (1/length(Tx)) * sum((real(Tx).^2 + imag(Tx).^2));SNRlin = power(10, SNR/10);%%noise = sqrt(Tx_Power/(2*SNRlin)) * [randn(1, length(Tx)) + j*randn(1, length(Tx))];noise = sqrt(Tx_Power/(2*SNRlin)) * randn(1, length(Tx));Rx = Tx + noise;